import React, { Component } from 'react';
import {connect} from 'react-redux';
import SearchInput from '../../components/SearchInput/SearchInput';
import {searchRequest} from './../../store/actions/search';
import ImagesList from '../ImagesList/ImagesList';

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      searchTerm: '',
      imgaesList:[]
    }
  }
  componentDidMount = () =>{
    // const {searchRequest} =this.props;
    // searchRequest({query:'cars'})
  }


  componentDidUpdate = (prevProps) =>{
    console.log('test')
    const {result} = this.props;
   if(prevProps.result !== this.props.result){
        console.log('test')
        console.log(this.props.result)
        this.setState({
          ImagesList:result
        }.console.log(this.state.result))
   }
  }
    handleChange = (e) =>{
    this.setState({
      searchTerm:e.target.value
    })
  }
  handleSubmit = (e) => {
    e.preventDefault();
    const {searchRequest} =this.props;
    searchRequest({query:this.state.searchTerm,page:1,per_page:5})

  }
  render() {
    return (
      <>
        <h1>Serach for a photo</h1>
          <SearchInput handleChange={this.handleChange}
                       handleSubmit={this.handleSubmit}/>
          <ImagesList />
      </>
    )
  }
}

const mapStateToProps = ({locale:{lang}, search:{result, total, total_pages}}) =>({
lang,
result,
total,
total_pages,
})

export default connect(mapStateToProps,{searchRequest})(Home);
